const std::string& ShaderChunk::get(const std::string& key)
{
	static std::string dummy;
	ShaderChunkMap::iterator iter = _map.find(key);
	if (iter != _map.end())
	{
		return iter->second;
	}
	return dummy;
}

void ShaderChunk::registe(const std::string& key, const std::string& shader)
{
	_map[key] = shader;
}

