void ShaderLib::registe(const char* shaderId, const std::string& vertex, const std::string& fragment)
{
	ShaderObject shader;
	shader.vertex = vertex;
	shader.fragment = fragment;
	_map[shaderId] = shader;
}

ShaderObject* ShaderLib::getShaderObject(const std::string& shaderId)
{
	ShaderObject* ret = NULL;

	ShaderObjectMap::iterator iter = _map.find(shaderId);
	if (iter != _map.end())
	{
		return &(iter->second);
	}

	return ret;
}